mapper.update:
UPDATE product_image
image_url = #{imageUrl}, thumbnail_url = #{thumbnailUrl}, is_primary = #{isPrimary},
WHERE product_id = #{productId}

mapper.select:
SELECT DISTINCT p.*, pi.thumbnail_url FROM products p LEFT JOIN product_image pi ON p.product_id = pi.product_id WHERE 1=1
AND p.product_name LIKE CONCAT('%', #{productName}, '%')
AND p.product_id = #{productId}
AND p.product_keyword LIKE CONCAT('%', #{productKeyword}, '%')
AND p.create_date = #{createDate}
AND pi.thumbnail_url IS NOT NULL ORDER BY p.create_date DESC

mapper.insert:
INSERT INTO product_image (
product_id, image_url, thumbnail_url, is_primary, created_at
) VALUES (
#{productId}, #{imageUrl}, #{thumbnailUrl}, #{isPrimary}, #{createdAt}
)

mapper.update:
UPDATE products
SET product_status = #{status}
WHERE product_id = #{productId}
