mapper.delete:
DELETE FROM products
WHERE product_id IN

    #{item}

mapper.select:
SELECT p.*, pi.thumbnail_url FROM products p LEFT JOIN product_image pi ON p.product_id = pi.product_id WHERE 1=1
AND p.product_name LIKE CONCAT('%', #{productName}, '%')
AND p.product_id = #{productId}
AND p.product_keyword LIKE CONCAT('%', #{productKeyword}, '%')
AND p.create_date = #{createDate}

SELECT
    p.*, 
    pi.thumbnail_url
FROM products p
LEFT JOIN product_image pi ON p.product_id = pi.product_id
WHERE p.product_category = #{productCategory}
AND p.product_category_details = #{productCategoryDetails}
ORDER BY p.create_date DESC;

mapper.insert:
INSERT INTO product_image (
product_id, image_url, thumbnail_url, is_primary, created_at
) VALUES (
#{productId}, #{imageUrl}, #{thumbnailUrl}, #{isPrimary}, #{createdAt}
)
